# --- 3. Tree Selector UI ---
TARGET_APPS = [] # Stores (Category, AppName, Path)

def create_app_selector():
    print(f"üìÇ Ê≠£Âú®ËÆÄÂèñÊ†πÁõÆÈåÑ: {BASE_PATH} ...")
    try:
        categories = list_folders(site_id, BASE_PATH)
    except Exception as e:
        print(f"‚ùå ËÆÄÂèñÂ§±Êïó: {e}")
        return

    ui_container = widgets.VBox()
    app_checkboxes = []

    # Header
    ui_container.children += (widgets.HTML("<h3>üìÇ App ÈÅ∏ÊìáÂô® (Ë´ãÂ±ïÈñã Category ‰∏¶ÂãæÈÅ∏ App)</h3>"),)

    for cat in categories:
        # Ë∑≥ÈÅéÁ≥ªÁµ±Ë≥áÊñôÂ§æ
        if cat['name'] in ["Forms", "_private"] or "audit" in cat['name'].lower():
            continue

        # Category Row
        cat_label = widgets.HTML(f"<b>üìÅ {cat['name']}</b>", layout=widgets.Layout(width='100px'))
        btn_expand = widgets.Button(description="‚ûï Â±ïÈñã", button_style='info', layout=widgets.Layout(width='80px'))
        app_list_box = widgets.VBox(layout=widgets.Layout(margin='0 0 0 30px', display='none')) # È†êË®≠Èö±Ëóè

        # Logic to load apps
        def on_expand(b, cat_name=cat['name'], container=app_list_box, btn=btn_expand):
            if btn.description == "‚ûï Â±ïÈñã":
                btn.description = "‚è≥"
                sub_path = f"{BASE_PATH}/{cat_name}"
                try:
                    apps = list_folders(site_id, sub_path)
                    checks = []
                    if not apps:
                        checks.append(widgets.Label("(ÁÑ°Ë≥áÊñôÂ§æ)"))

                    for app in apps:
                        if app['name'] in ["Forms", "_private"]: continue
                        cb = widgets.Checkbox(value=False, description=app['name'], indent=False)
                        cb.app_data = (cat_name, app['name'], f"{sub_path}/{app['name']}")
                        checks.append(cb)
                        app_checkboxes.append(cb)

                    container.children = tuple(checks)
                    container.layout.display = 'block'
                    btn.description = "‚ûñ Êî∂Ëµ∑"
                except Exception as e:
                    container.children = (widgets.Label(f"Error: {e}"),)
                    btn.description = "‚ùå"
            else:
                # Toggle visibility
                if container.layout.display == 'none':
                    container.layout.display = 'block'
                    btn.description = "‚ûñ Êî∂Ëµ∑"
                else:
                    container.layout.display = 'none'
                    btn.description = "‚ûï Â±ïÈñã"

        btn_expand.on_click(on_expand)
        ui_container.children += (widgets.HBox([btn_expand, cat_label]), app_list_box)

    # Footer Actions
    btn_confirm = widgets.Button(description="‚úÖ Á¢∫Ë™çÈÅ∏Êìá", button_style='success', layout=widgets.Layout(margin='20px 0'))
    output_area = widgets.Output()

    def on_confirm(b):
        global TARGET_APPS
        TARGET_APPS = [cb.app_data for cb in app_checkboxes if cb.value]
        with output_area:
            clear_output()
            if not TARGET_APPS:
                print("‚ö†Ô∏è Êú™ÈÅ∏Êìá‰ªª‰Ωï AppÔºÅ")
            else:
                print(f"üéØ Â∑≤ÈÅ∏Êìá {len(TARGET_APPS)} ÂÄã Apps„ÄÇË´ãÂü∑Ë°å Cell 4 ÈñãÂßãËôïÁêÜ„ÄÇ")
                for c, a, p in TARGET_APPS[:5]:
                    print(f" - [{c}] {a}")
                if len(TARGET_APPS) > 5: print(" ...")

    btn_confirm.on_click(on_confirm)

    display(ui_container, btn_confirm, output_area)

create_app_selector()
